import React, { Component } from 'react'
import Notifications from './Notifications.js'
import ProjectList from '../projects/ProjectList'
import {connect} from 'react-redux'
import {firestoreConnect} from 'react-redux-firebase'
import {compose}  from 'redux'
import { Redirect } from 'react-router-dom'
import {NavLink} from 'react-router-dom'




class Dashboard extends Component {
	render(){
		const {projects, auth, notifications} = this.props;
		if (!auth.uid) return <Redirect to='/signin' />
		return(
			<div>
				<div className="theme">
					<div className="branding">
						<h1 className="black-text center aa">PROSE</h1>
						<h6 className="black-text center bb">“I would define ... the Poetry of words as The Rhythmical Creation of Beauty.” — Edgar Allan Poe, from "The Poetic Principle."</h6>
					</div>

				</div>

				<div className="dashboard">
					<div className="row">
						<div className="col s12 m9">
							<ProjectList projects={projects} />
						</div>
						<div className="col s12 m2 offset-m1 notifs">
							<Notifications notifications={notifications} />
							<div class="container">
								<a class="waves-effect waves-light btn-large pulse createnew red"><i class="material-icons right">create</i><h3>CREATE POST  </h3></a>
							</div>
						</div>
					</div>

				</div>
			</div>



		)
	}
}



const mapStateToProps = (state) => {
	console.log(state)
	return {
		projects: state.firestore.ordered.projects,
		auth: state.firebase.auth,
		notifications: state.firestore.ordered.notifications
	}
}

export default compose(
	connect(mapStateToProps),
	firestoreConnect([
		{collection: 'projects', orderBy: ['createdAt','desc']},
		{collection: 'notifications' , limit: 5, orderBy: ['time','desc']}
	])
)(Dashboard)

