//responsibe for each individual page that we get

import React from 'react'
import { connect } from 'react-redux'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import { Redirect } from 'react-router-dom'
import moment from 'moment'

const ProjectDetails = (props) => {
  const { project,auth } = props;
  const id = props.match.params.id;
  if (!auth.uid) return <Redirect to='/signin' />	
  if (project) {
    return (
		<div class="container section project-details">
			<div class="col s12 m7 indirec">
				<h4 class="header">INDIVIDUAL RECORDS</h4>
				<div class="card horizontal">
					<div class="card-image">
						<img className="indiphoto" src={project.photo}></img>
					</div>
					<div class="card-stacked">
						<div class="card-content">
							<h6 className="namehome"><i class="material-icons small prefix details">account_circle</i>  {project.title}</h6>
          					<p className="addresshome"><i class="material-icons small details">home</i>  {project.content}</p>
						</div>
						<div class="card-action">
							<p className="black-text">Posted by {project.authorFirstName} {project.authorLastName}</p>
							<p className="black-text">{moment(project.createdAt.toDate()).calendar()}</p>
						</div>
					</div>
				</div>
			</div>
		</div>
    )
  } else {
    return (
      <div className="container center">
        <p>Loading project...</p>
      </div>
    )
  }
}





const mapStateToProps = (state, ownProps) => {
  // console.log(state);
  const id = ownProps.match.params.id;
  const projects = state.firestore.data.projects;
  const project = projects ? projects[id] : null
  return {
    project: project,
	auth: state.firebase.auth
  }
}

export default compose(
  connect(mapStateToProps),
  firestoreConnect([{
    collection: 'projects'
  }])
)(ProjectDetails)
